import React, { useState } from 'react'
import { List } from "./List";
import { v4 as uuid } from 'uuid'


export const Board = () => {
  const listId = uuid();
  const [list, setList] = useState([ {id:listId, draggableId:`list${listId}`, key: listId}]);
  const addList = () => {
    setList([...list, { id: listId, draggableId: `list${listId}`, key: listId}]);
    console.log("here"+list);
  };

  return (
    <div className='board'>
        <div className='list__container'>

          {list.map((listItem) => (<List key={listItem.key} list={list} setList={setList} listItem={listItem} />))
          }

        </div>
        <div className="add-list__button__wrapper">
            <button onClick={addList} className="add-list__button">
              +
            </button>
        </div>
    </div>
  )
}
