import React, { useState } from 'react'
import { List } from "./List";
import { AddListButton } from './AddListButton';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';


export const Board = () => {
  const [list, setList] = useState([{ id: 0, draggableId: "item0" }]);

  return (
    <DragDropContext>
      <Droppable droppableId='droppable' direction='horizontal'>
        {(provided) => {
          <div className='board' {...provided.droppableProps} ref={provided.innerRef}>
          <div className='list__container'>
            {list.map((listItem) => (<List key={listItem.key} list={list} setList={setList} listItem={listItem} />))
            }
          </div>
            <AddListButton list={list} setList={setList} />
      </div>
        }}
      </Droppable>

    </DragDropContext>
  )
}
