import React from 'react'
import { Draggable } from "react-beautiful-dnd";

export const Task = ( { task, taskList, setTaskList }) => {

  const handlleDelete = (id) => {
    setTaskList(taskList.filter((task) => task.id !== id));
  };
  const handleDrag = (e) => {
    const remove = taskList.splice(e.source.index, 1);
    setTaskList(taskList.splice(e.destination.index, 0, remove));
  }

  return (
    <Draggable index={task.id} draggableId={task.draggableId} onChange={handleDrag}>
        {(provided) => (
            <div className="task" key={task.id} ref={provided.innerRef}
            {...provided.draggableProps}
            {...provided.dragHandleProps}>
                <p>{ task.text }</p>
                <button class="delete-button" onClick={() => handlleDelete(task.id)}><p>Ã—</p></button>
            </div>
        )}
    </Draggable>
  )
}
