import React, { useState } from 'react'
import { List } from "./List";
import { AddListButton } from './AddListButton';
import { DragDropContext } from 'react-beautiful-dnd';


export const Board = () => {
  const [list, setList] = useState([{ id: "0", draggableId: "item0" }]);

  return (
    <DragDropContext>

          <div className='board'>
          <div className='list__container'>
            {list.map((listItem, index) => (<List key={listItem.id} list={list} setList={setList} listItem={listItem} />))
            }
          </div>
            <AddListButton list={list} setList={setList} />
      </div>

    </DragDropContext>
  )
}
