import React, { useState } from 'react'

export const TaskAddInput = ({inputText, setInputText, taskList, setTaskList}) => {
  const [isClick, setIsClick] = useState(false);

  const handleClick = () => {
      setIsClick(true);
  };
  const handleChange = (e) => { setInputText(e.target.value)};
  const handleSubmit = (e) => {
      e.preventDefault();
      setTaskList([...taskList, { text: inputText }]);
      setInputText("");
  };
  const handleBlur = (e) => {
      setIsClick(false);
  }; 

return (

  <div onClick={handleClick}>
      <form onSubmit={handleSubmit}>
          <input type="text" className="text-input" autoFocus onChange={handleChange} onBlur={handleBlur} value={inputText} maxLength="20"></input>
      </form>
  </div>
)
}
