import React, { useState } from 'react'
import { List } from "./List";
import { v4 as uuid } from 'uuid'


export const Board = () => {
  const [taskCardList, setTaskCardList] = useState([{ id: 0, draggableId: 0 }]);
  const addTaskCard = () => {
    const listId = uuid();
    setTaskCardList([...taskCardList, { id: listId, draggableId: listId}]);
  };


  return (
    <div className='board'>
        <div className='list__container'>
      <List />

          {/* {listItem.map((listItem) => (<List />))} */}

        </div>

            <button onClick={addTaskCard} className="add-list__button">
              +
            </button>
    </div>
  )
}
