import React, { useState } from 'react'
import { List } from "./List";
import { v4 as uuid } from 'uuid'
import { AddListButton } from './AddListButton';


export const Board = () => {
  const [list, setList] = useState([]);
  const addList = () => {
    const listId = uuid();
    setList([...list, { id: listId, draggableId: `list${listId}`, key: listId}]);
  };


  return (
    <div className='board'>
        <div className='list__container'>

          {list.map((list) => (<List key={list.key} />))}

        </div>
        <AddListButton list={list} setList={setList} />
        <div className="add-list__button__wrapper">
            <button onClick={addList} className="add-list__button">
              +
            </button>
        </div>
    </div>
  )
}
