import React, { useState } from 'react'

export const TaskAddInput = () => {
  const [isClick, setIsClick] = useState(false);
  const [inputTask, setInputTask] = useState("Study");

  const handleClick = () => {
      setIsClick(true);
  };
  const handleChange = (e) => { setInputTask(e.target.value)};
  const handleSubmit = (e) => {
      e.preventDefault();
      setIsClick(false);
  };
  const handleBlur = (e) => {
      setIsClick(false);
  }; 

return (

  <div onClick={handleClick}>
      {isClick ? <form onSubmit={handleSubmit}>
          <input type="text" className="text-input" autoFocus onChange={handleChange} onBlur={handleBlur} value={inputTask} maxLength="20"></input>
      </form> : <h3 className="list-title">{inputTask}</h3> }
  </div>
)
}
