import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import React, { useState } from "react";
import "./App.css";

function App() {
  // eslint-disable-next-line no-undef
  const [items] = useState(["item0, item1, item2"]);
  const onDragEnd = (result) => {
    const remove = items.splice(result.source.index, 1);
    console.log(result.source.index);
    console.log(result.destination.index);
    items.splice(result.destination.index, 0, remove[0]);
  };
  return (
    <div>
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="droppable">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              <Draggable draggableId="item0" index={0}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    className="bg-indigo-700 font-semibold text-white py-2 px-4 w-1/5 rounded"
                  >
                    item0
                  </div>
                )}
              </Draggable>
              <Draggable draggableId="item1" index={1}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    className="bg-indigo-700 font-semibold text-white py-2 px-4 w-1/5 rounded"
                  >
                    item1
                  </div>
                )}
              </Draggable>
              <Draggable draggableId="item2" index={2}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    className="bg-indigo-700 font-semibold text-white py-2 px-4 w-1/5 rounded"
                  >
                    item2
                  </div>
                )}
              </Draggable>
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default App;
